import axios from "axios";

const BASE_URL = "http://localhost:5000/api/";
const TOKEN = 
"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYxOWFkMzk0M2I1YjJkYTU2YWJiYmU1NiIsImlzQWRtaW4iOmZhbHNlLCJpYXQiOjE2Mzc4MzUwMTgsImV4cCI6MTYzODA5NDIxOH0.3Wqpo83O7SbWjM7ekg10wytVk4VpyDmBJdt1CtM7CcQ"



export const publicRequest = axios.create({
    baseURL: BASE_URL,
});

export const userRequest = axios.create({
    baseURL: BASE_URL,
    headers: {token: `Bearer ${TOKEN}`}
});
