import axios from "axios";

const BASE_URL = "http://localhost:5000/api/";
const TOKEN =  "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYxOWMwZTFjOTA5Nzg3ZTdkYjIzZTUwNSIsImlzQWRtaW4iOnRydWUsImlhdCI6MTYzNzYyMTQ4MCwiZXhwIjoxNjM3ODgwNjgwfQ.24KvI5tIHRvYcsDswXZ63z0R-t9T_v__R7eL08ObJ6g";



export const publicRequest = axios.create({
    baseURL: BASE_URL, 
});

export const userRequest = axios.create({
    baseURL: BASE_URL,
    headers: {token: `Bearer ${TOKEN}`}
});
