import React from 'react';
import { BsFacebook, BsTwitter, BsYoutube } from 'react-icons/bs';
import styled from 'styled-components';

const Container = styled.div`
display:flex;

`;
const Left = styled.div`
flex:1;
display:flex;
flex-direction:column;
padding:20px;
`;
const Logo = styled.h1`

`;
const Desc = styled.p`
flex:1
`;
const SocialContainer = styled.div`
display:flex;
`;
const SocialIcon = styled.div`
width:40px;
height:40px;
border-raduis:50%;
color:white;
background-color: #${props=>props.color};
display:flex;
align-items:center;
justify-content:center;
margin-right:20px;

`;
const Center = styled.div`
flex:1
`
const Right = styled.div`
flex:1;
`

 const Footer = () => {
    return (
        <Container>
            <Left>
                <Logo>TCHAD.SHOP</Logo>
                <Desc>
                    Lorem ipsum dolor sit amet, consectetur adipisicing elit. Recusandae quidem est debitis,
                     eveniet, iure minus sunt nostrum perferendis consectetur, 
                    officiis fugit. Quisquam quis sequi, nisi sint perspiciatis odio molestias eos.
                </Desc>
                <SocialContainer>
                    <SocialIcon color="3b5999">
                        <BsFacebook/>
                    </SocialIcon>
                    <SocialIcon color="3b">
                        <BsYoutube/>
                    </SocialIcon>
                    <SocialIcon>
                        <BsTwitter/>
                    </SocialIcon>
                </SocialContainer>
            </Left>
            <Center></Center>
            <Right></Right>
        </Container>
    )
}

export default Footer;
