import  {AiOutlineSearch,AiOutlineShoppingCart}  from  "react-icons/ai";
import React from 'react';
import styled from "styled-components";
import { red } from "@mui/material/colors";
import {mobile} from "../responsive";
import { BsBadge4K } from "react-icons/bs";


const Container= styled.div`
          height:70px;       
          ${mobile({height:"50px"})}   
          position:relative;      
`;
const Wrapper = styled.div`
        padding: 10px 20px;
        display:flex;
        align-item:center;
        justify-content:space-between;
        ${mobile({padding:"10px 0px" })}
       
`;
const Left= styled.div`
        flex:1;
        display:flex;
        align-item:center;
        position: relative;
        top:10px;
        font-weight:bold;
`
const Logo = styled.div`
     font-weight:bold;
     font-family: 'Henny Penny', cursive;
     font-size:40px;
     
     ${mobile({fontSize:"24px"})};
`;
const Shop= styled.span`
color:red;
`
const Center= styled.div`
text-align:center;
flex:1;
align-item:center;
`
const Right= styled.div`
flex:1;
display:flex;
align-item:center;
justify-content:flex-end;
position:relative;
top:10px;
${mobile({flex:2,justifyContent:"center"})}
`
const MenuItem = styled.div`
    fontsize:20px;
    cursor: pointer;
    font-weight:bolder;
    margin-left:25px;
    ${mobile({fontSize:"12px", marginLeft:"10px"})}
`
const Languages = styled.div`
  font-size:20px;
  cursor : pointer;
  ${mobile({display:"none"})}
  position:relative;
  top:1px;
  font-weight:bolder;
`
const SearchContainer = styled.div`
  border: 5px double #12c2e9;
  display:flex;
  align-item:center;
  margin-left:20px;
  padding:7px;
  height:20px;
  border-radius:10%;
`
const Input = styled.input`
   border:none;
   width:200px;
   
   ${mobile({width:"50px"})}
`

const Navbar = () => {
    return (
        <Container>
            <Wrapper>
                <Left>
                    <Languages>En</Languages>
                    <SearchContainer>
                        <Input placeholder="Search" />
                        <AiOutlineSearch style={{color:"red", fontSize: "20px"}}/>  
                    </SearchContainer>
                </Left>
                <Center>
                    <Logo>BEST-<Shop>SHOP</Shop></Logo>
                </Center>
                <Right>
                    <MenuItem>REGISTER</MenuItem>
                    <MenuItem>SIGN IN</MenuItem>
                    <MenuItem>
                    <BsBadge4K>
                        <AiOutlineShoppingCart style={{color:"red", fontSize: "30px"}} />
                    </BsBadge4K>
                   
                    </MenuItem>
                </Right>
            </Wrapper>
        </Container>
    )
}

export default Navbar;
